/// <reference types="node" />
import { IncomingMessage, ServerResponse } from 'http';
import { IAgent } from '../IAgent';
import { DateTimeValue } from '../configuration/DateTimeValue';
/**
 *  A summary of a single part in a multi-part/form body
 */
export interface IPartSummary {
    headers: {
        [key: string]: string[];
    };
    bodyStartIndex?: number;
    bodyEndIndex?: number;
    bodyLength?: number;
}
export interface IServerRequestInspector {
    init(agent: IAgent): void;
    requestStart(req: IncomingMessage, res: ServerResponse, requestStartTime: DateTimeValue): any;
    requestEnd(req: IncomingMessage, res: ServerResponse, content: Array<any>, size: number, requestStartTime: DateTimeValue, multiPartSummaries?: IPartSummary[]): void;
    responseStart(req: IncomingMessage, res: ServerResponse, responseStartTime: DateTimeValue): void;
    responseEnd(req: IncomingMessage, res: ServerResponse, content: Array<any>, size: number, responseStartTime: DateTimeValue): void;
}
