declare module "@glimpse/glimpse" {
    import { IAgent, IAgentOptions } from '@glimpse/glimpse-agent-node/IAgent';
    import { IServer, IServerOptions } from '@glimpse/glimpse-server/src/IServer';

    /**
     * The singleton Glimpse agent instance.
     */
    export const agent: IAgent;

    /**
     * The singleton Glimpse server instance.
     */
    export const server: IServer;

    /**
     * Represents the set of options that can be passed to Glimpse.
     */
    interface IGlimpseOptions {        

        /**
         * (Optional) The options passed to the Glimpse agent.
         */
        agent?: IAgentOptions;

        /**
         * (Optional) The options passed to the Glimpse server.
         */
        server?: IServerOptions;
    }

    /**
     * Initializes the Glimpse agent and server.
     *
     * By default, a Glimpse server will be launched in-process and attached as an additional endpoint of the application.
     *
     * @param options (Optional) A set of options to be passed to Glimpse.
     */
    export function init(options?: IGlimpseOptions): void;
}
